INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (1, 'punxsutawney-phil', 'Phil', 'Punxsutawney Phil', 'Punxsutawney', 'Pennsylvania', 'USA', 'https://www.groundhog.org/history-past-predictions', '', 'https://www.groundhog.org/prognostication', 1, 'Groundhog', '1', 'Punxsutawney Phil is a superstar prognosticating groundhog from Punxsutawney, Pennsylvania. He makes his predictions from his burrow on Gobler’s Knob. According to legend, Phil has made weather prognostications since 1886, owing his improbably long life to the miraculous “groundhog nog” administered at the annual Groundhog Picnic.', 'https://groundhog-day.com/images/ghogs/punxsutawney-phil.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (2, 'octoraro-orphie', 'Orphie', 'Octoraro Orphie', 'Quarryville', 'Pennsylvania', 'USA', 'http://home.ptd.net/\%7Emsvitale/index.html', '', 'https://lancasteronline.com/octoraro-orphies-2022-prognostication-at-the-slumbering-groundhog-lodge-photos/collection_bcba45f4-844a-11ec-9598-236672ff5898.html', 0, 'Taxidermied groundhog', '1', 'Octoraro Orphie, from Quarryville Pennsylvania, is a world-renowned weather prognosticator and “One True Groundhog.” Other so-called groundhogs draw more celebrity endorsements, but Orphie is not distracted by the worldly pursuits of fame and fortune (at least partially due to his being stuffed and mounted). He is a sharp dresser, often spotted in his dapper top hat and bow tie.', 'https://groundhog-day.com/images/ghogs/octoraro-orphie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (3, 'wiarton-willie', 'Willie', 'Wiarton Willie', 'Wiarton', 'Ontario', 'Canada', 'https://www.southbrucepeninsula.com/en/explore-and-play/wiarton-willie.aspx#Past-predictions', '', 'https://www.southbrucepeninsula.com/en/explore-and-play/wiarton-willie.aspx#Prediction-morning-February-2nd', 1, 'Groundhog', '1', 'Wiarton Wille is Canada’s most famous prognosticating groundhog, from the town of Wiarton, Ontario. Every February 2nd, Willie announces his prediction in Groundhogese to The Mayor of the Land and of the Marmot, the only person who can speak Groundhogese.', 'https://groundhog-day.com/images/ghogs/wiarton-willie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (4, 'jimmy-the-groundhog', 'Jimmy', 'Jimmy the Groundhog', 'Sun Prairie', 'Wisconsin', 'USA', 'https://www.cityofsunprairie.com/840/Groundhog-Day', 'visit@cityofsunprairie.com', 'https://www.marshfieldnewsherald.com/story/life/be-wisconsin/2022/02/02/groundhog-day-2022-sun-prairies-jimmy-delivers-his-prognostication/9254422002/', 1, 'Groundhog', '1', 'Jimmy the Groundhog is based in Sun Prairie, Wisconsin, the proclaimed “World Headquarters of the Groundhog.” His annual emergence from the ground on February 2nd foretells the coming of spring.  Jimmy has an extremely high accuracy rate on his annual predictions, making him an outstanding leader in meteorological circles. He occasionally bites mayors’ ears.', 'https://groundhog-day.com/images/ghogs/jimmy-the-groundhog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (5, 'concord-charlie', 'Charlie', 'Concord Charlie', 'Athens', 'West Virginia', 'USA', 'https://www.concord.edu/data/news/2022/concord-charlie-predicts-an-early-spring.aspx', '', 'https://www.concord.edu/data/news/2022/concord-charlie-predicts-an-early-spring.aspx ', 0, 'Presumed groundhog', '1', 'Concord Charlie is a presumed groundhog from Athens, West Virginia. In a tradition dating back to 1978, Charlie meets with the President of Concord University and relays his predictions regarding the duration of winter. The President then joins a slew of guests at the University’s annual Groundhog Day Breakfast and shares Charlie’s predictions. Notably, Charlie has never been seen by anyone except the President and presumably other university staff; we believe him to be a groundhog based on these few eyewitness accounts.', 'https://groundhog-day.com/images/ghogs/concord-charlie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (6, 'buckeye-chuck', 'Chuck', 'Buckeye Chuck', 'Marion', 'Ohio', 'USA', 'https://en.wikipedia.org/wiki/Buckeye_Chuck#Past_predictions', '', 'https://twitter.com/OHBuckeyeChuck/status/1488861276780392452', 1, 'Groundhog', '1', 'Buckeye Chuck is a weather-predicting groundhog in Marion, Ohio. In 1979, the Ohio legislature named Chuck the official groundhog for the Ohio state. Buckeye Chuck lives a comfortable, celebrity lifestyle — only working one day a year — but he doesn’t let the fame get to his head.', 'https://groundhog-day.com/images/ghogs/buckeye-chuck.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (7, 'general-beauregard-lee', 'Beau', 'General Beauregard Lee', 'Jackson', 'Georgia', 'USA', 'https://twitter.com/genbeaulee', '', 'https://twitter.com/genbeaulee/status/1488854634626174979', 1, 'Groundhog', '1', 'General Beauregard Lee is Georgia’s Official Weather Prognosticator, considered the principal prognosticator for the southern USA. General Beau lives comfortably in his groundhog-sized mansion known as Weathering Heights, where he enjoys naps, digging tunnels, and hashbrowns.', 'https://groundhog-day.com/images/ghogs/general-beauregard-lee.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (8, 'french-creek-freddie', 'Freddie ', 'French Creek Freddie', 'Upshur County', 'West Virginia', 'USA', 'https://wvdnr.gov/french-creek-freddie-facts-to-get-you-ready-for-groundhog-day/ ', '', 'https://www.wvnews.com/news/wvnews/french-creek-freddie-west-virginias-resident-groundhog-meteorologist-predicts-six-more-weeks-of-winter/article_01898694-8451-11ec-93a6-93d86d5ec5e9.html', 1, 'Groundhog', '1', 'French Creek Freddie is a weather-predicting groundhog from Upshur County, West Virginia. Unlike his unseen state compatriot Concord Charlie, French Creek Freddie is a visible groundhog, who issues predictions annually from his home at the West Virginia State Wildlife Center.', 'https://groundhog-day.com/images/ghogs/french-creek-freddie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (9, 'gertie-the-groundhog', 'Gertie', 'Gertie the Groundhog', 'Hanna City', 'Illinois', 'USA', 'https://www.facebook.com/wildlifeprairiepark', '', 'https://www.hoiabc.com/2022/02/02/gertie-groundhog-predicts-early-spring/', 1, 'Groundhog', '1', 'Gertie the Groundhog is a celebrated meteorologist from Wildlife Prairie State Park, Illinois. Since 1993, Gertie has been predicting the weather on February 2nd from her wildlife chalet, inspiring local musician Barry Cloyd to write the original, hit song “Wake-Up Gertie.”', 'https://groundhog-day.com/images/ghogs/gertie-the-groundhog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (10, 'dunkirk-dave', 'Dave', 'Dunkirk Dave', 'Dunkirk', 'New York', 'USA', 'https://www.dunkirkdave.com', '', 'https://www.dunkirkdave.com/groundhogs-day-2022/', 1, 'Groundhog', '1', 'Dunkirk Dave, from Dunkirk, New York, is the second longest prognosticating groundhog in the world, second only to Punxsutawney Phil. His owner, Bob Will, has been handling and helping groundhogs and other wildlife since he was a child, making Will and Dave the perfect pairing. Dave loves breakfasting on cooked oatmeal.', 'https://groundhog-day.com/images/ghogs/dunkirk-dave.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (11, 'staten-island-chuck', 'Chuck', 'Staten Island Chuck', 'New York City', 'New York', 'USA', 'https://en.wikipedia.org/wiki/Staten_Island_Chuck#Past_predictions', '', 'http://www.statenislandzoo.org/groundhog-day/', 1, 'Groundhog', '1', 'Staten Island Chuck — also known as Charles G. Hogg — is a resident groundhog at Staten Island Zoo in New York City. He is one of the most accurate groundhogs, with an 85\% accuracy rate. Chuck bit the hand of Mayor Michael Bloomberg and was subsequently the victim of an alleged manslaughter by succeeding Mayor Bill De Blasio.', 'https://groundhog-day.com/images/ghogs/staten-island-chuck.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (12, 'lander-lil', 'Lil', 'Lander Lil', 'Lander', 'Wyoming', 'USA', 'https://www.facebook.com/BLMWyoming', '', 'https://cowboystatedaily.com/2022/02/02/bill-sniffin-lander-lil-predicts-early-spring-in-wyoming-we-hope/', 0, 'Statue of a prairie dog', '1', 'Lander Lil, a bronze statue of a prairie dog, has been predicting the future of winter weather for Wyoming since 1984. There really was a real Lander Lil before the installation of a larger-than-life bronze statue, which stands lookout from her perch on the grounds of the local Post Office.', 'https://groundhog-day.com/images/ghogs/lander-lil.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (13, 'manitoba-merv', 'Merv', 'Manitoba Merv', 'Stonewall', 'Manitoba', 'Canada', 'https://twitter.com/OakHammockMarsh', '', 'https://twitter.com/OakHammockMarsh/status/1488877411827605505', 0, 'Groundhog golf club cover', '1', 'Groundhog puppet Manitoba Merv has been predicting the arrival of spring at the Oak Hammock Marsh Interpretive Centre for about 25 years. Merv is a puppet — technically a golf club cover — which is a bonus, because he lives forever and doesn’t need to be awakened from hibernation for a prediction. Merv began his storied career as an actor in puppet shows, but has since evolved into a Manitoba legend with a near flawless prediction rate.', 'https://groundhog-day.com/images/ghogs/manitoba-merv.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (14, 'shubenacadie-sam', 'Sam', 'Shubenacadie Sam', 'Shubenacadie', 'Nova Scotia', 'Canada', 'https://data.novascotia.ca/Lands-Forests-and-Wildlife/Shubenacadie-Wildlife-Park-Groundhog-Day-Predictio/4y37-h2yu', '', 'https://twitter.com/ShubenacadieSam/status/1488845184418959360', 1, 'Groundhog', '1', 'Shubenacadie Sam is a weather-predicting groundhog who resides at the Shubenacadie Wildlife Park in Nova Scotia. Living on the eastern edge of Canada, Sam makes the first prediction in North America every year at 8:00 am AST. Sam is active on Twitter and enjoys yoga and meditation.', 'https://groundhog-day.com/images/ghogs/shubenacadie-sam.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (15, 'woodstock-willie', 'Willie', 'Woodstock Willie', 'Woodstock', 'Illinois', 'USA', 'https://www.facebook.com/WoodstockGroundhogDays/', 'bellairsrick@gmail.com', 'https://abc7chicago.com/groundhog-day-2022-woodstock-willie-shadow/11532000/', 1, 'Groundhog', '1', 'Woodstock Willie is the official weather-predicting groundhog of Woodstock, Illinois. Woodstock is the filming location of the Bill Murray 1993 classic movie, “Groundhog Day”. Woodstock’s Groundhog Day ceremony is well attended, attracting with hundreds of spectators and featuring a Groundhog Polka Band.', 'https://groundhog-day.com/images/ghogs/woodstock-willie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (16, 'sir-walter-wally', 'Wally', 'Sir Walter Wally', 'Raleigh', 'North Carolina', 'USA', 'https://twitter.com/sirwalterw?lang=en', '', 'https://twitter.com/SirWalterW/status/1488957818942246916', 1, 'Groundhog', '1', 'Sir Walter Wally the official prognosticating groundhog from Raleigh, North Carolina, where he lives at the Museum of Natural Sciences. Wally is a media darling and is regularly featured on The Weather Channel’s list of national Groundhog Day representatives. He is also very active on Twitter where he loves publishing his predictions and subtweeting Punxsutawney Phil.', 'https://groundhog-day.com/images/ghogs/sir-walter-wally.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (17, 'poor-richard', 'Richard', 'Poor Richard', 'York', 'Pennsylvania', 'USA', 'http://www.yorkslumberinggroundhoglodge.com', '', 'https://www.fox43.com/article/news/local/punxsutawney-phil-poor-richard-groundhog-day-early-spring/521-e8a323c0-5331-47c0-8afd-1364f62dce1c', 0, 'Taxidermied groundhog', '1', 'Poor Richard is a taxidermied groundhog who prognosticates for the Slumbering Groundhog Lodge, in York, Pennsylvania. The York Slumbering Groundhog Lodge prides itself on carrying on the tradition of recognizing the importance of the groundhog in predicting the weather for the remaining 6 weeks of winter. Very rarely has York’s “Poor Richard” been wrong.', 'https://groundhog-day.com/images/ghogs/poor-richard.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (18, 'balzac-billy', 'Billy', 'Balzac Billy', 'Balzac', 'Alberta', 'Canada', 'https://www.facebook.com/bluegrasscgy', '', 'https://www.facebook.com/bluegrasscgy/posts/pfbid02Bf8bNUkaBiRpN87VXErLmR94ShZvxpPZLLoZ6o46C55h6AejUsDeTaAhviBG78iWl', 0, 'Person in a gopher suit', '1', 'Balzac Billy is the “Prairie Prognosticator”, a man-sized gopher mascot who prognosticates weather on Groundhog Day from the town of Balzac, Alberta. Billy is not a real groundhog because groundhogs are not native to Alberta. Instead, Billy is more properly a Richardson Ground Squirrel (ie, a gopher) who lives in a man-sized burrow outside the Blue Grass Nursery & Garden Centre. Billy eats seeds, leaves, roots and flowers, as well as some insects, and of course his annual Groundhog Day Breakfast!', 'https://groundhog-day.com/images/ghogs/balzac-billy.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (19, 'uni-the-groundhog', 'Uni', 'Uni the Groundhog', 'Myerstown', 'Pennsylvania', 'USA', 'https://www.facebook.com/Grundsau-Lodsch-17-Om-Union-Kanal-598077903658052/', '', 'https://www.facebook.com/598077903658052/videos/466995694904148/', 0, 'Taxidermied groundhog', '1', 'Every year on February 2nd, Myerstown’s favorite groundhog “Uni” is placed on a raft for a ceremonial ride down the Tulpehocken Creek.  After his ride, he makes his weather prediction for the next six weeks. Since 1981, the Union Kanaal Grundsau Lodsch No. 17 has hosted the ceremony, which is performed in both English and the Pennsylvania Dutch language.', 'https://groundhog-day.com/images/ghogs/uni-the-groundhog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (20, 'grover-and-sue', 'Grover and Sue', 'Grover and Sue', 'Pine Grove', 'Pennsylvania', 'USA', 'https://www.facebook.com/SweetArrowLake', '', 'https://www.standardspeaker.com/news/grover-the-groundhog-sweet-arrow-sue-predict-early-spring-phil-disagrees/article_d8668981-1203-5581-8a7b-af13a2c46c5e.html', 0, 'Taxidermied groundhogs', '1', 'Grover the Groundhog and Sweet Arrow Sue are a taxidermied groundhog couple representing Groundhog Lodge no. 5, from Pine Grove, Pennsylvania. Grover met with tragedy in a roadside accident but doesn’t let that get in the way of his duties as a public figure. He is an ambassador for his species, he predicts the weather, and he promotes recycling because he is recycled. Grover Pinebush was officially married to Sweet Arrow Sue in 2011, making them only married groundhog couple in Pennsylvania.', 'https://groundhog-day.com/images/ghogs/grover-and-sue.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (21, 'chuckles-the-groundhog', 'Chuckles', 'Chuckles the Groundhog', 'Manchester', 'Connecticut', 'USA', 'https://lutzmuseum.org/special-events/groundhog-day/', '', 'https://www.journalinquirer.com/connecticut_and_region/chuckles-predicts-6-more-weeks-of-winter-watch-the-video/article_171aa93a-8423-11ec-9c00-13ee4160e3ad.html', 1, 'Groundhog', '1', 'Chuckles (XI) presides over the annual Groundhog Day ceremony in Connecticut. Before making his prediction, the 8-month-old groundhog was sworn in as the official state groundhog by the former town mayor, then whispered his prediction into the ear of current mayor. Chuckles comes from a long line of furry prognosticators and resides at the Lutz Children’s Museum, the traditional residence of the Chuckles’.', 'https://groundhog-day.com/images/ghogs/chuckles-the-groundhog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (22, 'holtsville-hal', 'Hal', 'Holtsville Hal', 'Holtsville', 'New York', 'USA', 'https://www.facebook.com/brookhavenwildlifecenter', '', 'https://tbrnewsmedia.com/tag/holtsville-hal/', 1, 'Groundhog', '1', 'Holtsville Hal is the true-blue, first-rate North American Groundhog for the proud residents of Holtsville, New York. Hal’s prognostications are presented by Superintendent of Highways Daniel P. Losquadro. (The hot drink of the event is “HALuccino!) Hal’s introductory jingle goes: “Will he see it or will he not? Trust Holtzville Hal, he’s who we’ve got.”', 'https://groundhog-day.com/images/ghogs/holtsville-hal.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (23, 'malverne-mel', 'Malverne Mel', 'Malverne Mel', 'Malverne', 'New York', 'USA', 'https://www.facebook.com/crossroadsfarmatgrossmanns', '', 'https://greaterlongisland.com/holtsville-hal-predicts-an-early-spring-on-groundhog-day-malverne-mel-disagrees/', 1, 'Groundhog', '1', 'Groundhog Day with Malverne Mel is a tradition more than 2 decades old in Malverne, New York. Not happy with only predicting the weather, Mel is an art connoisseur and also hosts a poster contest. Mel is also a huge fan of the Bill Murray movie, “Groundhog Day.”', 'https://groundhog-day.com/images/ghogs/malverne-mel.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (24, 'ms-g', 'Ms. G', 'Ms. G', 'Lincoln', 'Massachusetts', 'USA', 'https://www.facebook.com/MassAudubonDrumlinFarm', 'http://www.marmotadventures.com/php/mail_request.php?stormy', 'https://www.masslive.com/weather/2022/02/massachusetts-state-groundhog-ms-g-disagrees-with-punxsutawney-phil-says-spring-has-come.html', 1, 'Groundhog', '1', 'Ms. G has been the official state groundhog of Massachusetts since 2014, after a successful nomination effort from Hunnewell Elementary School students. Ms. G has health issues that prevent her from living alone in the wild, but she fills her day with visiting schools and community groups in her role as an ambassador of the natural world.', 'https://groundhog-day.com/images/ghogs/ms-g.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (25, 'stormy-marmot', 'Stormy', 'Stormy Marmot', 'Aurora', 'Colorado', 'USA', 'http://www.marmotadventures.com/php/index.php', '', 'http://blog.marmotadventures.com/', 0, 'Plush yellow-bellied marmot', '1', 'Stormy Marmot is a plush yellow-bellied marmot from Aurora, Colorado. He is one of the few famous groundhogs west of the Mississippi. When he is not predicting the upcoming forecast, Stormy loves travelling the world (voyaging from Texas to Alaska to Hong Kong) and updating his fans on his various social media accounts.', 'https://groundhog-day.com/images/ghogs/stormy-marmot.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (26, 'flatiron-freddy', 'Freddy', 'Flatiron Freddy', 'Boulder', 'Colorado', 'USA', 'https://twitter.com/boulderosmp', '', 'https://twitter.com/boulderosmp/status/1488908742565318657?lang=en', 0, 'Taxidermied yellow-bellied marmot', '1', 'Flatiron Freddy is a top hat-wearing, yellow-bellied marmot who became a Groundhog Day weather prognosticator about a decade ago. Freddy is stuffed — not with the secret wisdom of unforetold weather events, but with actual stuffing, having died some years ago. Freddy generally chooses to arrive in style: sometimes flying in via zipline, rolling up in a monster truck, or sliding in on his snowboard.', 'https://groundhog-day.com/images/ghogs/flatiron-freddy.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (27, 'fred-la-marmotte', 'Fred', 'Fred la Marmotte', 'Val-d’Espoir', 'Quebec', 'Canada', 'https://www.fredlamarmotteofficielleduquebec.ca', '', 'https://globalnews.ca/news/8588505/quebec-fred-la-marmotte-groundhog-day-2022/', 1, 'Groundhog', '1', 'Fred la marmotte is a prognosticating Quebecois groundhog from Val-d’Espoir in the Gaspésie, Quebec. Overwhelmed by his gift of clairvoyance, Fred relies upon his handler and a mascot in his likeness to announce his prediction. Fred is unique in being the only weather-predicting groundhog whose habitat is located on a World Heritage Site, the “Géoparc mondial UNESCO de Percé.”', 'https://groundhog-day.com/images/ghogs/fred-la-marmotte.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (28, 'snohomish-slew', 'Slew', 'Snohomish Slew', 'Snohomish', 'Washington', 'USA', 'https://www.facebook.com/Thefrogturtlerescuelady/', 'thefroglady@justfrogsfoundation.org', 'https://www.facebook.com/Thefrogturtlerescuelady/posts/pfbid0SeN5JFodvWUFdu3fzfN4RoQhvbGsrnobgv2rUykPszNcrwaPwRv93nM65FSZe1yml', 0, 'Bullfrog', '1', 'Snohomish Slew, a frognosticating bullfrog, makes an annual Groundfrog Day weather forecast for the Pacific Northwest in downtown Snohomish. There is much accompanying pomp and ceremony. From Thayer Cueter, Slew’s handler: “The pinnacle celebration moment [is] when Slew whispers his frogcast to his favourite scribe in Groundfrogese. [The] gathered crowd will be ribbeted! by every transcribed word from Slew as he accurately predicts our winter weather fate for six more weeks.”', 'https://groundhog-day.com/images/ghogs/snohomish-slew.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (29, 'two-rivers-tunnel', 'Tunnel', 'Two Rivers Tunnel', 'Cape Breton', 'Nova Scotia', 'Canada', 'https://www.facebook.com/TwoRiversWildlifePark/', '', 'https://www.saltwire.com/atlantic-canada/news/six-more-weeks-of-winter-cape-bretons-two-rivers-tunnel-makes-annual-groundhog-day-prediction-100688061/', 1, 'Groundhog', '1', 'Two Rivers Tunnel is the official prognosticator for Cape Breton, located in Two Rivers Wildlife Park. Tunnel the groundhog lives in a charming green house and speaks in a distinct Groundhogese dialect — most likely native to Cape Breton, where he has been making predictions for over 20 years.', 'https://groundhog-day.com/images/ghogs/two-rivers-tunnel.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (30, 'woody-the-woodchuck', 'Woody', 'Woody the Woodchuck', 'Howell', 'Michigan', 'USA', 'https://www.facebook.com/howellnaturecenter', '', 'https://www.mlive.com/news/ann-arbor/2022/02/its-official-woody-the-groundhog-declares-six-more-weeks-of-winter.html', 1, 'Groundhog', '1', 'Woody the Woodchuck, Michigan’s official clairvoyant groundhog, has been prognosticating for over 2 decades. Unlike the ‘shadow’ technique used by most groundhogs, Woody’s forecast is determined by the time she spends outside on a ceremonial tree stump: 30 seconds or longer means an early spring, any less means a chilly six more weeks of winter.', 'https://groundhog-day.com/images/ghogs/woody-the-woodchuck.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (31, 'bee-cave-bob', 'Bob', 'Bee Cave Bob', 'Bee Cave', 'Texas', 'USA', 'https://twitter.com/beecavebob', '', 'https://www.kxan.com/news/texas/texas-icon-bee-cave-bob-an-armadillo-predicts-an-early-spring/', 0, 'Armadillo', '1', 'Bee Cave Bob has helped Texans predict the early arrival of spring or the prolongment of winter since 2010. Bob is part of a Groundhog Day secessionist movement: with founder T. Booth rechristening it “Armadillo Day,” and stating “we don’t need no mangy groundhog in Pennsylvania predicting the weather for us.” In addition to weather forecasts, Bob also predicts the upcoming year’s partisan lean.', 'https://groundhog-day.com/images/ghogs/bee-cave-bob.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (32, 'chilly-charlie', 'Charlie', 'Chilly Charlie', 'Woodstock', 'Ontario', 'Canada', 'https://www.facebook.com/events/302076975234159', '', 'https://www.facebook.com/1047HeartFM/posts/4885586941490590', 0, 'Person in a groundhog suit', '1', 'Chilly Charlie is a groundhog mascot who makes the annual Groundhog Day prediction for Woodstock, Ontario. Woodstock’s traditional Groundhog Day celebration/pancake breakfast started in 2003, and has in recent years been modified to a drive-through format.', 'https://groundhog-day.com/images/ghogs/chilly-charlie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (33, 'beardsley-bart', 'Bart', 'Beardsley Bart', 'Bridgeport', 'Connecticut', 'USA', 'https://twitter.com/beardsleybart', '', 'https://www.beardsleyzoo.org/press/beardsley-bart-connecticuts-prognosticating-prairie-dog-makes-his-groundhog-day-weather-prediction', 0, 'Prairie dog', '1', 'Beardsley Bart is the principal prognosticating prairie dog at Connecticut’s Beardsley Zoo. His official title is Senior Spring Arrival Specialist. Every year, Beardsley Bart says that spring is coming eventually, and he has always been 100 percent correct.', 'https://groundhog-day.com/images/ghogs/beardsley-bart.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (34, 'sand-mountain-sam', 'Sam', 'Sand Mountain Sam', 'Albertville', 'Alabama', 'USA', 'https://twitter.com/wqsb_1051/status/1488525058704384003', '', 'https://whnt.com/news/sand-mountain-sam-returns-and-predicts-an-early-spring/', 0, 'Opossum', '1', 'Sand Mountain Sam is a legendary prognosticating opossum from Albertville, Alabama, who has been making predictions for over a decade. In 2020, the state game warden nearly cancelled the tradition (usually, having a possum as a pet is against the law), but in 2022 Sam returned, emerging from a whiskey barrel to predict an early spring.', 'https://groundhog-day.com/images/ghogs/sand-mountain-sam.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (35, 'stonewall-jackson', 'Stonewall', 'Stonewall Jackson', 'Wantage', 'New Jersey', 'USA', 'https://www.facebook.com/spacefarms', '', 'https://www.facebook.com/spacefarms/posts/pfbid0cRMSN3cTFLd41ynzM4efNeLQV4gdT2157ttbuZLckMRTWvcb3JVrxhQNG4ridfpjl', 1, 'Groundhog', '1', 'Stonewall Jackson (VI) is a weather-predicting groundhog from Wantage, New Jersey, where he lives on Space Farms. Stonewall is still “in training” but he is from a proud prognosticating dynasty so he should have a leg up on other would-be weather-guessers.', 'https://groundhog-day.com/images/ghogs/stonewall-jackson.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (36, 'bowman-bill', 'Bill', 'Bowman Bill', 'Stephens City', 'Virginia', 'USA', 'https://www.handleyregional.org/events/groundhog-day-bowman-bill', '', 'https://www.facebook.com/handleyregional/videos/638800130760830/', 0, 'Plush groundhog puppet', '1', 'Bowman Bill is the resident plush puppet prognosticator at Bowman Library, in Stephens City, Virginia. Bill’s Groundhog Day ceremony includes awards, crafts, stories and ice cream: if Bill sees his shadow, the ice cream is topped with chocolate sauce. Not one to hog the limelight, Bill shares his Groundhog Day with two live groundhogs, Tiva and Luna, who come to the library to observe Bill’s technique first hand.', 'https://groundhog-day.com/images/ghogs/bowman-bill.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (37, 'fenwick-flossie', 'Flossie', 'Fenwick Flossie', 'Pelham', 'Ontario', 'Canada', 'https://www.facebook.com/fabulousfenwicklions/', '', 'https://thevoiceofpelham.ca/2022/02/10/fenwick-flossie-predicts-early-spring/', 0, 'Person in a groundhog suit', '1', 'Fenwick Flossie is an adult-sized groundhog mascot organized by the local Lion’s Club in Fenwick, Ontario. Flossie is a non-gender mascot, given life by an octogenarian Lion’s club member who has suited up for the role each year for nearly three decades. The club prefers to not release the person’s identity to protect the mystery of who is channelling the woodchuck spirit.', 'https://groundhog-day.com/images/ghogs/fenwick-flossie.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (38, 'lawrenceville-lucy', 'Lucy', 'Lawrenceville Lucy', 'Lawrenceville', 'Pennsylvania', 'USA', 'https://www.facebook.com/LawrencevilleLucy', '', 'https://www.facebook.com/LawrencevilleLucy/posts/pfbid02iWbJe14NWuPwf2F41ohzJcwDSBZgp7zcDBLzinW31bFaahYjjpXNyFfs8TJiTQml', 1, 'Groundhog', '1', 'Lawrenceville Lucy is a wild groundhog living in a burrow overlooking Cowanesque Lake near Lawrenceville, Pennsylvania. Every year on February 2nd, she gives a very spirited prediction and updates her Facebook page with the news. Uncommonly in wild groundhogs, Lucy is partial to hot chocolate with marshmallows and chocolate sprinkles, but doesn’t let it interfere with her work of making accurate, unbiased predictions. ', 'https://groundhog-day.com/images/ghogs/lawrenceville-lucy.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (39, 'chesapeake-chuck', 'Chuck', 'Chesapeake Chuck', 'Newport News', 'Virginia', 'USA', 'https://www.facebook.com/VirginiaLivingMuseum', '', 'https://www.wavy.com/news/local-news/newport-news/chesapeake-chuck-the-groundhog-to-make-winter-prediction-for-hampton-roads-on-wednesday/', 1, 'Groundhog', '1', 'Chesapeake Chuck is Virginia Living Museum’s furry prognosticator of prognosticators, hailing from Newport News, VA. Not just limited to weather predictions, Chuck is also able to foresee the winner of the Super Bowl. In order to stay in tip-top shape, Chuck dines on “restaurant quality” carrots and sweet potatoes.', 'https://groundhog-day.com/images/ghogs/chesapeake-chuck.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (40, 'scramble-the-duck', 'Scramble', 'Scramble the Duck', 'Eastford', 'Connecticut', 'USA', 'https://scrambletheduck.org', '', 'https://scrambletheduck.org/news/2022/02/01/scramble-forecasts-six-more-weeks/', 0, 'Pekin duck', '1', 'Scramble the Duck is a prognosticating Pekin duck from Eastford, Connecticut who makes an official prediction on “Duck Day” every February 2nd. Billed as “an accurate alternative to the groundhog,” Scramble has an impressive perfect record of weather prognostications. According to his website, “Scramble can predict the weather because he has the most superior education,” in addition to the innate fact that ducks “live above ground, [so] they know more about the weather.”', 'https://groundhog-day.com/images/ghogs/scramble-the-duck.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (41, 'snowy-the-prairie-dog', 'Snowy', 'Snowy the Prairie Dog', 'Pend Oreille County', 'Washington', 'USA', 'http://www.climatehawk.org/groundhog.html', '', 'http://www.climatehawk.org/groundhog.html', 0, 'Prairie dog', '1', 'Snowy the Prairie Dog makes an annual weather prediction from Pend Oreille Country, Washington. Snowy and Flurries are a tag-team of prognosticating prairie dogs with their very own website and an impressive accuracy rating. Bob Lutz, traffic broadcaster for 920 News Now, maintains their list of historical predictions as well as an “Accuracy Score” for each year: a level of accountability that few other prognosticators can meet.', 'https://groundhog-day.com/images/ghogs/snowy-the-prairie-dog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (42, 'benny-the-bass', 'Benny', 'Benny the Bass', 'Buckeye Lake', 'Ohio', 'USA', 'https://www.facebook.com/BuckeyeLakeWinterfest', '', 'https://www.facebook.com/photo/?fbid=542305224204717', 0, 'Largemouth bass', '1', 'Benny the Bass is a foreseeing fish whose annual weather prediction kicks off Winterfest in Buckeye Lake, Ohio. Unlike the traditional ‘shadow’ method preferred by landgoing prognosticators, Benny’s prediction is based on his breakfast. “I pour a bunch of minnows into this big tank with a live bass,” says Mike Fornataro, Chair of Winterfest, “Then people chant, Take the bait, spring can’t wait.’ The bass has three minutes to eat a minnow. If he does, it’s an early spring. If he doesn’t, it’s six more weeks of winter.”', 'https://groundhog-day.com/images/ghogs/benny-the-bass.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (43, 'harleysville-hank', 'Hank', 'Harleysville Hank', 'Harleysville', 'Pennsylvania', 'USA', 'https://www.facebook.com/harleysvillehank/', '', 'https://www.thereporteronline.com/2022/02/02/harleysville-hanks-groundhog-day-prognostication-is-for-an-early-spring/', 0, 'Plush groundhog', '1', 'Harleysville Hank is a plush groundhog who makes an annual prediction at the Mennonite Heritage Center in Harleysville, Pennsylvania. While it is hard to believe there could be anyone who doesn’t know about Harleysville Hank, his Facebook page says he’s single, enjoys tunnelling, and speaks Pennsylvania Dutch. Under awards, Hank has listed “Best Groundhog. Ever.” Hank’s Groundhog Day festivities include free coffee, hot chocolate, and kettle-cooked scrapple.', 'https://groundhog-day.com/images/ghogs/harleysville-hank.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (44, 'van-island-violet', 'Violet', 'Van Island Violet', 'Nanaimo', 'British Columbia', 'Canada', 'https://www.facebook.com/MarmotRecovery/', '', 'https://curiocity.com/bc-groundhog-day-2022/', 1, 'Groundhog', '1', 'Van Island Violet is a prognosticating marmot from Vancouver Island whose predictions are meticulously interpreted by the Marmot Recovery Foundation in Nanaimo, British Columbia. There are four species of marmots in Canada, but only the Vancouver Island Marmot (Marmota vancouverensis) is considered at risk. Adam Taylor, executive director of the nonprofit, believes that “a Vancouver Island marmot would be a better weather prognosticator for Vancouver Island than some Eastern marmot that has never been west of the Rockies.”', 'https://groundhog-day.com/images/ghogs/van-island-violet.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (45, 'potomac-phil', 'Phil', 'Potomac Phil', 'Washington DC', 'District of Columbia', 'USA', 'https://twitter.com/potomacphil', '', 'https://twitter.com/PotomacPhil/status/1488877212296175617', 0, 'Taxidermied groundhog', '1', 'Potomac Phil is a taxidermied groundhog who makes his weather proclamation in front of D.C.’s Dupont Circle fountain. He is “Washington’s most famous marmot” — a “humble, unflinching prognosticator,” who delights in corn cobs. While other groundhog prognosticators are fluent in Groundhogese, Phil prefers “non-verbal communication”.', 'https://groundhog-day.com/images/ghogs/potomac-phil.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (46, 'gordy-the-groundhog', 'Gordy', 'Gordy the Groundhog', 'Milwaukee', 'Wisconsin', 'USA', 'https://www.facebook.com/MilwaukeeCountyZoo', '', 'https://www.jsonline.com/story/life/wisconsin-family/2022/02/02/milwaukee-county-zoos-gordy-groundhog-predicts-springs-arrival/9230547002/', 1, 'Groundhog', '1', 'Gordy the Groundhog is the resident prognosticator at the Milwaukee County Zoo in Wisconsin. He was born in Indiana but moved to Milwaukee for his new job, where he makes friends quickly on account of his goofy and friendly personality. Gordy is a connoisseur of broccoli and taking naps.', 'https://groundhog-day.com/images/ghogs/gordy-the-groundhog.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (47, 'lucy-the-lobster', 'Lucy', 'Lucy the Lobster', 'Barrington', 'Nova Scotia', 'Canada', 'https://twitter.com/lucylobsterns', '', 'https://twitter.com/CailinONeil/status/1488894511107485702', 0, 'Atlantic lobster', '1', 'Lucy the Lobster makes Groundhog Day predictions on February 2nd to kick off the Lobster Crawl festival in Barrington, Nova Scotia. The Municipality of Barrington says Lucy is the only prognosticating crustacean to make a prediction in Canada. Bill Murray was reported as being bemused by the idea of a lobster forecaster.', 'https://groundhog-day.com/images/ghogs/lucy-the-lobster.png');
INSERT INTO groundhogs (id, slug, shortname, name, city, region, country, source, contact, currentPrediction, isGroundhog, type, active, description, image) VALUES (48, 'poppy-the-groundhog', 'Poppy', 'Poppy the Groundhog', 'Millersville', 'Pennsylvania', 'USA', 'https://www.facebook.com/PoppytheGroundhog/', 'info@acornacreswr.com', 'https://lancasteronline.com/poppy-the-groundhog-makes-her-2022-prediction-photos/collection_b0bcd23c-844d-11ec-b707-4b2629e94712.htm', 1, 'Groundhog', '1', 'Poppy the Groundhog is 3 years old (in 2022) is internationally known and Emmy nominated. Poppy is an educational ambassador with Acorn Acres Wildlife Rehabilitation in Millersville, PA. She starred in the 2020 Super Bowl commercial with Jeep and Bill Murray. Since her debut she has become quite the educator and does numerous educational appearance every month. Annually she holds her Groundhog Day Scavenger Hunt in downtown Lancaster, PA that runs for a solid week! She has her own line of merch available at Ville & Rue.', 'https://groundhog-day.com/images/ghogs/poppy-the-groundhog.png');
